{{! This template is used for email notification using Alert Target}}
<!-- Container -->
<div style="font-family: &quot;Trebuchet MS&quot;, &quot;Lucida Grande&quot;, &quot;Helvetica Neue&quot;, sans-serif;padding: 15px;background-color: #fafafa;">
  <!-- Notification -->
  <div style="width: 600px;margin: auto;background-color: #ffffff;box-shadow: 0 1px 1px rgba(0, 0, 0, 0.05);">
    <!-- Header -->
    <div style="background-color: #002538;padding: 20px;color: #ffffff;font-size: 120%;border-radius: 3px 3px 0 0;">
      {{{name}}}
    </div>
    <!-- Body -->
    <div style="padding: 20px;padding-bottom: 30px;border: 1px solid #eee;word-break: break-word;">
      {{#secureMetricDetails}}
        <strong>Details for this alert have been excluded from notifications. Go to the Wavefront UI for
          additional information.</strong>
      {{/secureMetricDetails}}

      {{#imageLinks}}
      <div style="text-align: right;">
        <img src="{{{.}}}" style="max-width: 100%;max-height: 100%;">
        <small style="font-size: 60%;color: #999;">Time in image is shown in GMT.</small>
      </div>
      {{/imageLinks}}

      <table>
        <tbody>
          <tr>
            <td style="font-size: 95%;padding: 15px 0;line-height: 1.5;width: 150px;vertical-align: top;">Reason</td>
            <td style="font-size: 95%;padding: 15px 0;line-height: 1.5;">{{reason}}</td>
          </tr>

          <tr>
            <td style="font-size: 95%;padding: 15px 0;line-height: 1.5;width: 150px;vertical-align: top;">Status</td>
            <td style="font-size: 95%;padding: 15px 0;line-height: 1.5;">{{headline}}</td>
          </tr>

          {{#severity}}
            <tr>
              <td style="font-size: 95%;padding: 15px 0;line-height: 1.5;width: 150px;vertical-align: top;">Severity</td>
              <td class="severity {{{severity}}}" style="font-weight: bold;font-size: 95%;padding: 15px 0; line-height: 1.5;
                  color:
                    {{#severitySevere}}#E42301;{{/severitySevere}}
                    {{#severityWarn}}#A86800;{{/severityWarn}}
                    {{#severitySmoke}}#637A83;{{/severitySmoke}}
                    {{#severityInfo}}#007DB3;{{/severityInfo}}
                  ">
                {{{severity}}}
              </td>
            </tr>
          {{/severity}}

          <tr>
            <td style="font-size: 95%;padding: 15px 0;line-height: 1.5;width: 150px;vertical-align: top;">Condition</td>
            <td style="font-size: 12px;padding: 3px 5px;line-height: 2;border: 1px solid #ccc;border-radius: 3px;margin: 12px 0;font-family: Consolas, Monaco, Courier, monospace;color: #494949;word-break: break-all;display: inline-block;background-color: #fafafa;">
              {{{condition}}}
            </td>
          </tr>

          {{! Alert Opened section }}
          {{#isAlertOpened}}
            <tr>
              <td style="font-size: 95%;padding: 15px 0;line-height: 1.5;width: 150px;vertical-align: top;">Affected Since</td>
              <td style="font-size: 95%;padding: 15px 0;line-height: 1.5;">{{sinceTime}}</td>
            </tr>

            <tr>
              <td style="font-size: 95%;padding: 15px 0;line-height: 1.5;width: 150px;vertical-align: top;">Event Started</td>
              <td style="font-size: 95%;padding: 15px 0;line-height: 1.5;">{{startedTime}}</td>
            </tr>
            <tr>
              <td style="font-size: 95%;padding: 15px 0;line-height: 1.5;width: 150px;vertical-align: top;">Sources/Labels<br />Affected</td>
              <td style="font-size: 12px;padding: 3px 5px;line-height: 2;border: 1px solid #ccc;border-radius: 3px;margin: 12px 0;font-family: Consolas, Monaco, Courier, monospace;color: #494949;word-break: break-all;display: inline-block;background-color: #fafafa;">
                {{{hostsFailingMessage}}}
              </td>
            </tr>

            <tr>
              <td style="font-size: 95%;padding: 15px 0;line-height: 1.5;width: 150px;vertical-align: top;">Sources/Labels in<br />Maintenance</td>
              <td style="font-size: 12px;padding: 3px 5px;line-height: 2;border: 1px solid #ccc;border-radius: 3px;margin: 12px 0;font-family: Consolas, Monaco, Courier, monospace;color: #494949;word-break: break-all;display: inline-block;background-color: #fafafa;">
                {{#trimTrailingComma}}
                  {{#inMaintenanceSeries}}
                    {{{.}}}<br />,
                  {{/inMaintenanceSeries}}
                {{/trimTrailingComma}}
              </td>
            </tr>

            <tr>
              <td style="font-size: 95%;padding: 15px 0;line-height: 1.5;width: 150px;
              vertical-align: top;">Source Tags <br />Affected</td>
              <td style="font-size: 12px;padding: 3px 5px;line-height: 2;border: 1px solid #ccc;border-radius: 3px;margin: 12px 0;font-family: Consolas, Monaco, Courier, monospace;color: #494949;word-break: break-all;display: inline-block;background-color: #fafafa;">
                {{#trimTrailingComma}}
                  {{#failingHostSourceTags}}
                    "Host: {{host}}, SourceTags: {{sourceTags}}"<br />,
                  {{/failingHostSourceTags}}
                {{/trimTrailingComma}}
              </td>
            </tr>
          {{/isAlertOpened}}


          {{! Alert Updated section }}
          {{#isAlertUpdated}}
            <tr>
              <td style="font-size: 95%;padding: 15px 0;line-height: 1.5;width: 150px;vertical-align: top;">Affected Since</td>
              <td style="font-size: 95%;padding: 15px 0;line-height: 1.5;">{{sinceTime}}</td>
            </tr>

            <tr>
              <td style="font-size: 95%;padding: 15px 0;line-height: 1.5;width: 150px;vertical-align: top;">Event Started</td>
              <td style="font-size: 95%;padding: 15px 0;line-height: 1.5;">{{startedTime}}</td>
            </tr>

            <tr>
              <td style="font-size: 95%;padding: 15px 0;line-height: 1.5;width: 150px;vertical-align: top;">Sources/Labels<br />Affected</td>
              <td style="font-size: 12px;padding: 3px 5px;line-height: 2;border: 1px solid #ccc;border-radius: 3px;margin: 12px 0;font-family: Consolas, Monaco, Courier, monospace;color: #494949;word-break: break-all;display: inline-block;background-color: #fafafa;">
                {{{hostsFailingMessage}}}
              </td>
            </tr>

            <tr>
              <td style="font-size: 95%;padding: 15px 0;line-height: 1.5;width: 150px;vertical-align: top;">Sources/Labels in<br />Maintenance</td>
              <td style="font-size: 12px;padding: 3px 5px;line-height: 2;border: 1px solid #ccc;border-radius: 3px;margin: 12px 0;font-family: Consolas, Monaco, Courier, monospace;color: #494949;word-break: break-all;display: inline-block;background-color: #fafafa;">
                {{#trimTrailingComma}}
                  {{#inMaintenanceSeries}}
                    {{{.}}}<br />,
                  {{/inMaintenanceSeries}}
                {{/trimTrailingComma}}
              </td>
            </tr>

            <tr>
              <td style="font-size: 95%;padding: 15px 0;line-height: 1.5;width: 150px;
              vertical-align: top;">Source Tags <br />Affected</td>
              <td style="font-size: 12px;padding: 3px 5px;line-height: 2;border: 1px solid #ccc;border-radius: 3px;margin: 12px 0;font-family: Consolas, Monaco, Courier, monospace;color: #494949;word-break: break-all;display: inline-block;background-color: #fafafa;">
                {{#trimTrailingComma}}
                  {{#newlyFailingHostSourceTags}}
                    "Host: {{host}}, SourceTags: {{sourceTags}}"<br />,
                  {{/newlyFailingHostSourceTags}}
                {{/trimTrailingComma}}
              </td>
            </tr>
          {{/isAlertUpdated}}


          {{! Alert Resolved section }}
          {{#isAlertResolved}}
            <tr>
              <td style="font-size: 95%;padding: 15px 0;line-height: 1.5;width: 150px;vertical-align: top;">Affected Since</td>
              <td style="font-size: 95%;padding: 15px 0;line-height: 1.5;">{{sinceTime}}</td>
            </tr>

            <tr>
              <td style="font-size: 95%;padding: 15px 0;line-height: 1.5;width: 150px;vertical-align: top;">Event Started</td>
              <td style="font-size: 95%;padding: 15px 0;line-height: 1.5;">{{startedTime}}</td>
            </tr>

            <tr>
              <td style="font-size: 95%;padding: 15px 0;line-height: 1.5;width: 150px;vertical-align: top;">Event Ended</td>
              <td style="font-size: 95%;padding: 15px 0;line-height: 1.5;">{{endedTime}}</td>
            </tr>

            <tr>
              <td style="font-size: 95%;padding: 15px 0;line-height: 1.5;width: 150px;vertical-align: top;">Sources/Labels<br />Recovered</td>
              <td style="font-size: 12px;padding: 3px 5px;line-height: 2;border: 1px solid #ccc;border-radius: 3px;margin: 12px 0;font-family: Consolas, Monaco, Courier, monospace;color: #494949;word-break: break-all;display: inline-block;background-color: #fafafa;">
                {{#trimTrailingComma}}
                  {{#recoveredHosts}}
                    {{{.}}},
                  {{/recoveredHosts}}
                {{/trimTrailingComma}}
              </td>
            </tr>

            <tr>
              <td style="font-size: 95%;padding: 15px 0;line-height: 1.5;width: 150px;
              vertical-align: top;">Source Tags <br />Recovered</td>
              <td style="font-size: 12px;padding: 3px 5px;line-height: 2;border: 1px solid #ccc;border-radius: 3px;margin: 12px 0;font-family: Consolas, Monaco, Courier, monospace;color: #494949;word-break: break-all;display: inline-block;background-color: #fafafa;">
                {{#trimTrailingComma}}
                  {{#recoveredHostSourceTags}}
                    "Host: {{host}}, SourceTags: {{sourceTags}}"<br />,
                  {{/recoveredHostSourceTags}}
                {{/trimTrailingComma}}
              </td>
            </tr>
          {{/isAlertResolved}}


          {{! Alert Maintenance section }}
          {{#isAlertMaintenance}}
            <tr>
              <td style="font-size: 95%;padding: 15px 0;line-height: 1.5;width: 150px;vertical-align: top;">Affected Since</td>
              <td style="font-size: 95%;padding: 15px 0;line-height: 1.5;">{{sinceTime}}</td>
            </tr>

            <tr>
              <td style="font-size: 95%;padding: 15px 0;line-height: 1.5;width: 150px;vertical-align: top;">Event Started</td>
              <td style="font-size: 95%;padding: 15px 0;line-height: 1.5;">{{startedTime}}</td>
            </tr>

            <tr>
              <td style="font-size: 95%;padding: 15px 0;line-height: 1.5;width: 150px; vertical-align: top;">Event Suspended</td>
              <td style="font-size: 95%;padding: 15px 0;line-height: 1.5;">{{endedTime}}</td>
            </tr>
          {{/isAlertMaintenance}}


          {{! Alert Snooze section }}
          {{#isAlertSnoozed}}
            <tr>
              <td style="font-size: 95%;padding: 15px 0;line-height: 1.5;width: 150px;vertical-align: top;">Snoozed Until</td>
              <td style="font-size: 95%;padding: 15px 0;line-height: 1.5;">{{snoozedUntilTime}}</td>
            </tr>

            {{#sinceTime}}
              <tr>
                <td style="font-size: 95%;padding: 15px 0;line-height: 1.5;width: 150px;vertical-align: top;">Affected Since</td>
                <td style="font-size: 95%;padding: 15px 0;line-height: 1.5;">{{sinceTime}}</td>
              </tr>
            {{/sinceTime}}

            {{#startedTime}}
              <tr>
                <td style="font-size: 95%;padding: 15px 0;line-height: 1.5;width: 150px;vertical-align: top;">Event Started</td>
                <td style="font-size: 95%;padding: 15px 0;line-height: 1.5;">{{startedTime}}</td>
              </tr>
            {{/startedTime}}
          {{/isAlertSnoozed}}

          {{#hasTracingPageLinks}}
            <tr>
              <td style="font-size: 95%;padding: 15px 0;line-height: 1.5;width: 150px;
              vertical-align: top;">Tracing Dashboard Links
              </td>
              <td style="font-size: 12px;padding: 3px 5px;line-height: 2;border: 1px solid #ccc;border-radius: 3px;margin: 12px 0;font-family: Consolas, Monaco, Courier, monospace;color: #494949;word-break: break-all;display: inline-block;background-color: #fafafa;">
                {{#tracingPageLinks}}
                  <a href="{{{.}}}" target="_blank" style="border-color: #007cbb;
              background-color: #007cbb;color: #ffffff;text-decoration: none;padding: 6px 12px;font-size: 11px; border-radius: 3px;">
