                {{/tracingPageLinks}}
              </td>
            </tr>
          {{/hasTracingPageLinks}}

          {{#hasRunbookLinks}}
            <tr>
              <td style="font-size: 95%;padding: 15px 0;line-height: 1.5;width: 150px;
              vertical-align: top;">Runbook Links </td>
              <td style="font-size: 12px;padding: 3px 5px;line-height: 2;border: 1px solid #ccc;border-radius: 3px;margin: 12px 0;font-family: Consolas, Monaco, Courier, monospace;color: #494949;word-break: break-all;display: inline-block;background-color: #fafafa;">
                {{#trimTrailingComma}}
                  {{#runbookLinks}}
                    {{{.}}},<br />
                  {{/runbookLinks}}
                {{/trimTrailingComma}}
              </td>
            </tr>
          {{/hasRunbookLinks}}

          {{#hasAlertTriageDashboardNameLinks}}
            <tr>
              <td style="font-size: 95%;padding: 15px 0;line-height: 1.5;width: 150px;
              vertical-align: top;">Alert Triage Dashboard Links
              </td>
              <td style="font-size: 12px;padding: 3px 5px;line-height: 2;border: 1px solid #ccc;border-radius: 3px;margin: 12px 0;font-family: Consolas, Monaco, Courier, monospace;color: #494949;word-break: break-all;display: inline-block;background-color: #fafafa;">
                {{#alertTriageDashboardNameLinks}}
