{
  "attachments": [
    {
      "fallback": "{{#jsonEscape}}{{{name}}}{{/jsonEscape}}",
      "pretext": "{{#jsonEscape}}{{{name}}}{{/jsonEscape}}",
      "color": "{{#jsonEscape}}{{#isAlertOpened}}{{#severitySevere}}danger{{/severitySevere}}{{#severityWarn}}warning{{/severityWarn}}{{/isAlertOpened}}{{#isAlertResolved}}good{{/isAlertResolved}}{{/jsonEscape}}",
      "mrkdwn_in": [
        "text",
        "pretext"
      ],
      "fields": [
        {
          "title": "Severity",
          "value": "{{#jsonEscape}}{{{severity}}}{{/jsonEscape}}",
          "short": true
        },
        {
          "title": "Status",
          "value": "{{#jsonEscape}}{{#isAlertOpened}}FIRING{{/isAlertOpened}}{{#isAlertResolved}}RESOLVED{{/isAlertResolved}}{{/jsonEscape}}",
          "short": true
        },
        {{#isAlertOpened}}
        {
          "title": "Failing Alert Series",
          "value": "{{#jsonEscape}}{{{hostsFailingMessage}}}{{/jsonEscape}}",
          "short": false
        },
        {{/isAlertOpened}}
        {{#isAlertResolved}}
        {
          "title": "Recovered Alert Series",
          "value": "{{#jsonEscape}}{{#trimTrailingComma}}{{#recoveredSeries}}{{{.}}},{{/recoveredSeries}}{{/trimTrailingComma}}{{/jsonEscape}}",
          "short": false
        },
        {{/isAlertResolved}}
        {
          "title": "Started Time",
          "value": "{{#jsonEscape}}{{{startedTime}}}{{/jsonEscape}}",
          "short": true
        },
        {{#isAlertResolved}}
        {
          "title": "Ended Time",
          "value": "{{#jsonEscape}}{{{endedTime}}}{{/jsonEscape}}",
          "short": true
        },
        {{/isAlertResolved}}
        {
          "title": "URL",
          "value": "<{{{url}}}>",
          "short": true
        },
      ]
    }
  ]
}

